+import type { StreamFn } from "@mariozechner/pi-agent-core";
+import type { Api, AssistantMessage, Model } from "@mariozechner/pi-ai";
+
+type ToolCallBlock = {
+  type?: unknown;
+  arguments?: unknown;
+  partialJson?: unknown;
+};
+
+function parseToolArgs(value: unknown): Record<string, unknown> | undefined {
+  if (!value) return undefined;
+  if (typeof value === "string") {
+    const trimmed = value.trim();
+    if (!trimmed) return undefined;
+    try {
+      const parsed = JSON.parse(trimmed);
+      if (parsed && typeof parsed === "object" && !Array.isArray(parsed)) {
+        return parsed as Record<string, unknown>;
+      }
+    } catch {
+      return undefined;
+    }
+    return undefined;
+  }
+  if (typeof value === "object" && !Array.isArray(value)) {
+    const record = value as Record<string, unknown>;
+    return Object.keys(record).length > 0 ? record : undefined;
+  }
+  return undefined;
+}
+
+function patchToolCallArgs(message: AssistantMessage): AssistantMessage {
+  if (!Array.isArray(message.content)) return message;
+  let changed = false;
+
+  const nextContent = message.content.map((block) => {
+    if (!block || typeof block !== "object") return block;
+    const record = block as ToolCallBlock & Record<string, unknown>;
+    if (record.type !== "toolCall") return block;
+    if (parseToolArgs(record.arguments)) return block;
+    const parsed = parseToolArgs(record.partialJson);
+    if (!parsed) return block;
+    changed = true;
+    return { ...record, arguments: parsed };
+  });
+
+  return changed ? { ...message, content: nextContent } : message;
+}
+
+export function wrapStreamFnToolCallArgs(streamFn: StreamFn): StreamFn {
+  return (model, context, options) => {
+    const stream = streamFn(model as Model<Api>, context, options);
+    const originalResult = stream.result.bind(stream);
+    stream.result = async () => patchToolCallArgs(await originalResult());
+    return stream;
+  };
+}
